package com.tinytongtong.thinkinjavapractice.test;

import java.io.UnsupportedEncodingException;
import java.net.URLDecoder;

/**
 * @Description: Base64 和 URL编码测试
 * @Author wangjianzhou@qding.me
 * @Date 2019/1/17 4:08 PM
 * @Version TODO
 */
public class Base64Test {
    public static void main(String[] args) {
        String base64 = "data:image/png;base64,";
        String urlEncoder = "iAC+IiAC+ORPQEI/of/2a6Q";
        try {
            urlEncoder = URLDecoder.decode(urlEncoder, "UTF-8");// base64直接decode，就会出现+变为空格的情况
//            urlEncoder = URLEncoder.encode(urlEncoder, "UTF-8");
        } catch (UnsupportedEncodingException e) {
            e.printStackTrace();
        }
        System.out.println("urlEncoder --> " + urlEncoder);
        System.out.println("base64.equals(urlEncoder) --> " + base64.equals(urlEncoder));
    }
}
